contracts/DonationAdmin.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./DonationStructs.sol";

/**
 * @title DonationAdmin
 * @dev 기부 플랫폼의 관리자 기능 컨트랙트
 * 프로젝트 생성, 상태 변경 등 관리자 권한이 필요한 기능들을 포함합니다.
 */
contract DonationAdmin is Ownable {
    // 플랫폼 수수료 관련 변수
    address public platformFeeRecipient;
    uint256 public platformFeePercent = 2; // 2% 기본 수수료
    
    // 컨트랙트 생성자
    constructor() Ownable(msg.sender) {
        platformFeeRecipient = msg.sender;
    }
    
    /**
     * @dev 플랫폼 수수료 설정
     * @param _feePercent 새로운 수수료 비율 (%)
     */
    function setPlatformFee(uint256 _feePercent) external onlyOwner {
        require(_feePercent <= 10, unicode"운영수수료는 10%를 넘길 수 없습니다.");
        platformFeePercent = _feePercent;
    }
    
    /**
     * @dev 수수료 수령자 변경
     * @param _recipient 새로운 수수료 수령자 주소
     */
    function setPlatformFeeRecipient(address _recipient) external onlyOwner {
        require(_recipient != address(0), unicode"주소는 0x0이 될 수 없습니다.");
        platformFeeRecipient = _recipient;
    }
    
    /**
     * @dev 새 프로젝트 생성 기능
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _createProject(
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 projectId,
        string memory _name,
        string memory _description,
        address payable _recipient,
        uint256 _targetAmount,
        uint256 _deadline
    ) internal {
        require(_targetAmount > 0, unicode"목표 기부 금액은 0eth이상입니다.");
        require(_deadline > block.timestamp, unicode"기부 마감일을 다시 확인해주세요.");
        require(_recipient != address(0), unicode"캠페인 주소는 0x0일 수 없습니다.");
        
        DonationStructs.Project storage newProject = projects[projectId];
        newProject.recipient = _recipient;
        newProject.name = _name;
        newProject.description = _description;
        newProject.targetAmount = _targetAmount;
        newProject.raisedAmount = 0;
        newProject.isActive = true;
        newProject.deadline = _deadline;
        newProject.createTime = block.timestamp;
        newProject.uniqueDonorCount = 0;
    }
    
    /**
     * @dev 프로젝트 활성화/비활성화 토글 기능
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _toggleProjectStatus(
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 _projectId,
        uint256 projectCount
    ) internal {
        require(_projectId > 0 && _projectId <= projectCount, unicode"프로젝트 id를 다시 입력해주세요.");
        projects[_projectId].isActive = !projects[_projectId].isActive;
    }
    
    /**
     * @dev 프로젝트 마감일 연장 기능
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _extendProjectDeadline(
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 _projectId,
        uint256 _newDeadline,
        uint256 projectCount
    ) internal {
        require(_projectId > 0 && _projectId <= projectCount, unicode"유효하지 않은 projectId입니다.");
        require(_newDeadline > block.timestamp, unicode"연장된 마감일 입력이 잘못되었습니다.");
        require(_newDeadline > projects[_projectId].deadline, unicode"연장된 마감일은 연장 전 마감일보다 길어야 합니다.");
        
        projects[_projectId].deadline = _newDeadline;
    }
    
    /**
     * @dev 프로젝트 목표 금액 증가 기능
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _increaseProjectTarget(
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 _projectId,
        uint256 _newTarget,
        uint256 projectCount
    ) internal {
        require(_projectId > 0 && _projectId <= projectCount, unicode"유효하지 않은 projectId입니다.");
        require(_newTarget > projects[_projectId].targetAmount, unicode"새로 설정할 목표 금액은 기존 금액보다 높아야 합니다.");
        
        projects[_projectId].targetAmount = _newTarget;
    }
    
    /**
     * @dev 비상 상황 시 자금 회수 기능
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _emergencyWithdraw(address payable _to, uint256 _amount) internal {
        _to.transfer(_amount);
        //컨트랙트에 남은 잔액을 _to 주소로 보내줌 
    }
}

contracts/DonationCore.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./ReentrancyGuard.sol";
import "./DonationStructs.sol";

/**
 * @title DonationCore
 * @dev 기부 플랫폼의 핵심 기능 컨트랙트
 * 기부 처리, 자금 인출 등 핵심 금융 로직이 포함됩니다.
 */
contract DonationCore is ReentrancyGuard {
    // 타임락 관련 상수
    // 기부금 인출 요청 후 24시간 후에만 실행 가능한 타임락 설정 -> 재진입 공격 방지 
    uint256 public constant WITHDRAWAL_TIMELOCK = 1 days; // 24시간 대기
    
    
    /**
     * @dev 기부 처리 기능
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _processDonation(
        mapping(uint256 => DonationStructs.Project) storage projects,
        mapping(address => DonationStructs.Donor) storage donors,
        mapping(uint256 => DonationStructs.Donation[]) storage projectDonations,
        uint256 _projectId,
        bool _isAnonymous,
        address _donor,
        uint256 _donationAmount,
        address _feeRecipient,
        uint256 _fee
    ) internal nonReentrant {
        // 수수료 전송
        if (_fee > 0) {
            payable(_feeRecipient).transfer(_fee);
        }
        
        DonationStructs.Project storage project = projects[_projectId];
        project.raisedAmount += _donationAmount;
        
        // 기부자 정보 업데이트
        DonationStructs.Donor storage donor = donors[_donor];
        donor.totalDonated += _donationAmount;
        
        // 중복 없이 기부한 프로젝트 추가
        // 기부한 적이 없는 프로젝트의 경우 플젝 id추가 donorExist등록, uniqueDonerCount증가
        if (!project.donorExists[_donor]) {
            donor.donatedProjects.push(_projectId);
            project.donorExists[_donor] = true;
            project.uniqueDonorCount++;
        }
        
        // 기부 내역 기록
        projectDonations[_projectId].push(DonationStructs.Donation({
            donor: _donor,
            projectId: _projectId,
            amount: _donationAmount,
            timestamp: block.timestamp,
            isAnonymous: _isAnonymous
        }));
        
        // 목표금액 달성 시 자동 종료
        if (project.raisedAmount >= project.targetAmount) {
            project.isActive = false;
        }
    }
    
    /**
     * @dev 기부금 인출 요청 처리
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
     //nonReentrant사용 이유
     //수수료송금,환불 등 이더 송금하는 로직 포함되어 있으므로
     
    function _requestWithdrawal(
        mapping(uint256 => DonationStructs.Project) storage projects,
        mapping(uint256 => DonationStructs.WithdrawalRequest) storage withdrawalRequests,
        uint256 _requestId,
        uint256 _projectId,
        uint256 _amount
    ) internal nonReentrant {
        DonationStructs.Project storage project = projects[_projectId];
        require(_amount > 0 && _amount <= project.raisedAmount, "Invalid amount");
        
        withdrawalRequests[_requestId] = DonationStructs.WithdrawalRequest({
            projectId: _projectId,
            amount: _amount,
            requestTime: block.timestamp,
            executed: false
        });
    }
    
    /**
     * @dev 기부금 인출 실행
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _executeWithdrawal(
        mapping(uint256 => DonationStructs.WithdrawalRequest) storage withdrawalRequests,
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 _requestId
    ) internal nonReentrant returns (address recipient, uint256 amount) {
        DonationStructs.WithdrawalRequest storage request = withdrawalRequests[_requestId];
        require(!request.executed, "Request already executed");
        require(block.timestamp >= request.requestTime + WITHDRAWAL_TIMELOCK, "Timelock period not passed");
        
        DonationStructs.Project storage project = projects[request.projectId];
        require(request.amount <= project.raisedAmount, "Insufficient funds");
        
        request.executed = true;
        project.raisedAmount -= request.amount;
        
        return (project.recipient, request.amount);
    }
    
    /**
     * @dev 프로젝트 취소 및 환불 처리
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _cancelProjectAndRefund(
        mapping(uint256 => DonationStructs.Project) storage projects,
        mapping(uint256 => DonationStructs.Donation[]) storage projectDonations,
        uint256 _projectId
    ) internal nonReentrant {
        DonationStructs.Project storage project = projects[_projectId];
        require(project.isActive, "Project is not active");
        
        project.isActive = false;
        
        // 모든 기부 내역 순회하며 환불
        DonationStructs.Donation[] storage donations = projectDonations[_projectId];
        for (uint256 i = 0; i < donations.length; i++) {
            DonationStructs.Donation storage donation = donations[i];
            payable(donation.donor).transfer(donation.amount);
        }
        
        // 프로젝트 잔액 초기화
        project.raisedAmount = 0;
    }
}

contracts/DonationPlatform.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Interfaces.sol";
import "./DonationStructs.sol";
import "./DonationUtils.sol";
import "./DonationAdmin.sol";
import "./DonationCore.sol";
import "./DonationQuery.sol";

/**
 * @title DonationPlatform
 * @dev 메인 기부 플랫폼 컨트랙트
 * 모든 모듈을 통합하여 완전한 기부 플랫폼 기능을 제공합니다.
 */
contract DonationPlatform is Ownable, DonationAdmin, DonationCore, DonationQuery {
    // 상태 변수
    uint256 public totalDonations; //총 기부 금액
    uint256 public projectCount;   //캠페인 개수
    uint256 public withdrawalRequestCount; //출금요청횟수
    
    // 저장소
    mapping(uint256 => DonationStructs.Project) public projects; //플젝번호로 플젝찾기
    mapping(address => DonationStructs.Donor) public donors; //donor주소로 donor 찾기
    mapping(uint256 => DonationStructs.Donation[]) public projectDonations; //각 project 별 donation개수
    mapping(uint256 => DonationStructs.WithdrawalRequest) public withdrawalRequests; // 인출 요청들 
    
    // 토큰 컨트랙트 주소
    IDonationToken public donationToken;
    IDonationCertificate public donationCertificate;
    
    // 이벤트 정의
    event ProjectCreated(uint256 indexed projectId, string name, address recipient, uint256 targetAmount, uint256 deadline);
    event DonationReceived(uint256 indexed projectId, address indexed donor, uint256 amount, bool isAnonymous);
    
    // 실제 인출 
    event FundsWithdrawn(uint256 indexed projectId, address recipient, uint256 amount);
    event ProjectStatusToggled(uint256 indexed projectId, bool isActive);
    event DonorNameSet(address indexed donor, string name);
    
    // 인출 요청 
    event WithdrawalRequested(uint256 indexed requestId, uint256 projectId, uint256 amount);
    event ProjectCancelled(uint256 indexed projectId);
    event ProjectDeadlineExtended(uint256 indexed projectId, uint256 newDeadline);
    event ProjectTargetIncreased(uint256 indexed projectId, uint256 newTarget);
    
    /**
     * @dev 컨트랙트 생성자
     */
     //상속받은 Ownable에서 owner 변수 설정
    constructor() DonationAdmin() {  //배포될 때 한 번 실행됨. 
        totalDonations = 0;
        projectCount = 0;
        withdrawalRequestCount = 0;
    }
    
    /**
     * @dev 토큰 컨트랙트 설정
     * @param _tokenAddress 토큰 컨트랙트 주소
     */
     //기부 토큰 컨트랙트의 주소를 설정하는 관리자용 함수, 외부에서만 호출 가능, 
    function setDonationToken(address _tokenAddress) external onlyOwner {
        donationToken = IDonationToken(_tokenAddress); 
        //실제 배포된 ERC20 토큰 컨트랙트 주소를 연결하는 역할
        //파라미터로 들어온 주소는 이미 배포된 ERC20토큰 컨트랙트 주소여야함. 
        //IDonationToken타입으로 형변환해서 donationToken에 저장 ->  donationToken.mint()처럼 외부 토큰 컨트랙트의 함수 호출 가능

    }
    //onlyOwner: Ownable에서 상속받은 제어자 (오직 owner만 호출 가능)
    
    /**
     * @dev NFT 컨트랙트 설정
     * @param _certAddress NFT 컨트랙트 주소
     */
    function setDonationCertificate(address _certAddress) external onlyOwner {
        donationCertificate = IDonationCertificate(_certAddress);
    }
    
    /**
     * @dev 기부자 이름 설정
     * @param _name 기부자 이름
     */
    function setDonorName(string memory _name) public {
        DonationStructs.Donor storage donor = donors[msg.sender]; //donor 구조체 가져오기
        donor.name = _name;
        donor.hasSetName = true; //이름은 optional이니까 
        
        emit DonorNameSet(msg.sender, _name);
    }
    
    /**
     * @dev 새 프로젝트 생성
     * @param _name 프로젝트 이름
     * @param _description 프로젝트 설명
     * @param _recipient 기금 수령자 주소
     * @param _targetAmount 목표 금액
     * @param _deadline 마감일
     * @return 생성된 프로젝트 ID
     */
    function createProject(
        string memory _name,
        string memory _description,
        address payable _recipient,
        uint256 _targetAmount,
        uint256 _deadline
    ) public onlyOwner returns (uint256) { //관리자만 호출 가능
        projectCount++;
        //플젝 생성하는 내부 함수 호출
        _createProject(projects, projectCount, _name, _description, _recipient, _targetAmount, _deadline);
        
        //이벤트 발생,블록체인에 기록 남김.ui나 블록탐색기에서 새 플젝 생성 이력 확인 가능
        emit ProjectCreated(projectCount, _name, _recipient, _targetAmount, _deadline);
        return projectCount;
    }
    
    /**
     * @dev 기부 기능
     * @param _projectId 기부할 프로젝트 ID
     * @param _isAnonymous 익명 기부 여부
     */
    function donate(uint256 _projectId, bool _isAnonymous) public payable {
        require(_projectId > 0 && _projectId <= projectCount,  unicode"존재하지 않는 projectId입니다.");
        require(projects[_projectId].isActive, unicode"현재 캠페인이 비활성상태입니다.");
        require(block.timestamp <= projects[_projectId].deadline,unicode"캠페인이 이미 만료료되었습니다.");
        require(msg.value > 0, unicode"기부금은 0eth보다 커야 합니다.");
        
        // 수수료 계산
        uint256 fee = (msg.value * platformFeePercent) / 100;
        uint256 donationAmount = msg.value - fee;
        
        // 기부 처리
        // DonationCore 컨트랙트 내부에 정의되어있음
        _processDonation(
            projects,
            donors,
            projectDonations,
            _projectId,
            _isAnonymous,
            msg.sender,
            donationAmount,
            platformFeeRecipient,
            fee
        );
        
        totalDonations += donationAmount; //총 기부금 누적
        

        //블록체인 로그에 남김 -> 이더스캔 / 프론트에서 조회 가능함.
        emit DonationReceived(_projectId, msg.sender, donationAmount, _isAnonymous);
        
        // // 리워드 토큰(ERC20) 발급함 (설정된 경우) 
        // if (address(donationToken) != address(0)) {
        //     //  1 ETH당 100 토큰 발급 -> 이거는 수정 가능함함
        //     uint256 tokenAmount = donationAmount * 100 / 1 ether;

        //     //앞에서 설정한 외부 ERC20 컨트랙트 호출출
        //     donationToken.mint(msg.sender, tokenAmount);
        // }
        
        // // NFT 인증서 발급 (금액이 0이더보다 많을 경우 & 인증서 발급용 nft컨트랙트가 설정된 경우)
        // if (donationAmount >= 0 ether && address(donationCertificate) != address(0)) {
            
        //     //nft 메타데이터 주소(url)을 동적으로 생성함
        //     //"https://donation-platform.example/certificate/3/1715600973"
        //     string memory tokenURI = string(abi.encodePacked(
        //         "https://donation-platform.example/certificate/", 
        //         DonationUtils.toString(_projectId),
        //         "/",
        //         DonationUtils.toString(block.timestamp)
        //     ));

        //     //기부자에게 NFT 한 장 발급, 메타데이터는 tokenURI로 링크되어있음.
        //     donationCertificate.mintCertificate(msg.sender, tokenURI);
        // }
    }
    
    /**
     * @dev 기부금 인출 요청
     * @param _projectId 프로젝트 ID
     * @param _amount 인출 금액
     */
    function requestWithdrawal(uint256 _projectId, uint256 _amount) public {
        require(_projectId > 0 && _projectId <= projectCount, unicode"projectID를 바르게 입력하세요.");
        require(msg.sender == projects[_projectId].recipient || msg.sender == owner(), unicode"인출 권한이 없습니다.");
        
        withdrawalRequestCount++;
        _requestWithdrawal(projects, withdrawalRequests, withdrawalRequestCount, _projectId, _amount);
        
        //누가 어떤 프로젝트에서 얼마를 요청했는지로그로 남겨서
        //프론트에서 대기 중인 요청 목록 표시하거나 이더스캔에서 확인 가능함.
        //실제 송금은 24시간 후에 가능함. 
        emit WithdrawalRequested(withdrawalRequestCount, _projectId, _amount);
    }
    
    /**
     * @dev 기부금 인출 실행
     * @param _requestId 인출 요청 ID
     */

     //기부금 인출 요청은 바로 실행되ㅣㅈ 않고
     //requestWithdrawal()함수는 쵸어만 기록하고, 실제 이더 받으려면 이 함수를 호출해야함. 
    function executeWithdrawal(uint256 _requestId) public {
        require(_requestId > 0 && _requestId <= withdrawalRequestCount, "Invalid request ID");
        
        (address recipient, uint256 amount) = _executeWithdrawal(withdrawalRequests, projects, _requestId);
        
        // 자금 전송
        payable(recipient).transfer(amount);
        
        emit FundsWithdrawn(withdrawalRequests[_requestId].projectId, recipient, amount);
    }
    
    /**
     * @dev 프로젝트 활성화/비활성화
     * @param _projectId 프로젝트 ID
     */
    function toggleProjectStatus(uint256 _projectId) public onlyOwner {
        _toggleProjectStatus(projects, _projectId, projectCount);
        emit ProjectStatusToggled(_projectId, projects[_projectId].isActive);
    }
    
    /**
     * @dev 프로젝트 마감일 연장
     * @param _projectId 프로젝트 ID
     * @param _newDeadline 새 마감일
     */
    function extendProjectDeadline(uint256 _projectId, uint256 _newDeadline) public onlyOwner {
        _extendProjectDeadline(projects, _projectId, _newDeadline, projectCount);
        emit ProjectDeadlineExtended(_projectId, _newDeadline);
    }
    
    /**
     * @dev 프로젝트 목표 금액 증가
     * @param _projectId 프로젝트 ID
     * @param _newTarget 새 목표 금액
     */
    function increaseProjectTarget(uint256 _projectId, uint256 _newTarget) public onlyOwner {
        _increaseProjectTarget(projects, _projectId, _newTarget, projectCount);
        emit ProjectTargetIncreased(_projectId, _newTarget);
    }
    
    /**
     * @dev 프로젝트 정보 조회
     * @param _projectId 프로젝트 ID
     * @return name 프로젝트 이름
     * @return description 프로젝트 설명
     * @return recipient 수혜자 주소
     * @return targetAmount 목표 금액
     * @return raisedAmount 현재까지 모금된 금액
     * @return isActive 프로젝트 활성화 여부
     * @return deadline 마감일 (타임스탬프)
     * @return createTime 생성일 (타임스탬프)
     * @return uniqueDonorCount 고유 기부자 수
     */
    function getProjectDetails(uint256 _projectId) public view returns (
        string memory name,
        string memory description,
        address recipient,
        uint256 targetAmount,
        uint256 raisedAmount,
        bool isActive,
        uint256 deadline,
        uint256 createTime,
        uint256 uniqueDonorCount
    ) {
        require(_projectId > 0 && _projectId <= projectCount, "Invalid project ID");
        return _getProjectDetails(projects, _projectId);
    }
    
    /**
     * @dev 프로젝트의 기부 내역 조회
     * @param _projectId 프로젝트 ID
     * @param _offset 시작 인덱스
     * @param _limit 조회할 항목 수
        * @return donors 기부자 주소 목록
        * @return amounts 기부 금액 목록
        * @return timestamps 기부 시간 목록
        * @return anonymousFlags 익명 기부 여부 목록
     */
    function getProjectDonations(uint256 _projectId, uint256 _offset, uint256 _limit) public view returns (
        address[] memory donors,
        uint256[] memory amounts,
        uint256[] memory timestamps,
        bool[] memory anonymousFlags
    ) {
        require(_projectId > 0 && _projectId <= projectCount, "Invalid project ID");
        return _getProjectDonations(projectDonations, _projectId, _offset, _limit);
    }
    
    /**
     * @dev 기부자 정보 조회
     * @param _donor 기부자 주소
     * @return name 기부자 이름
    * @return totalDonated 총 기부 금액
    * @return donatedProjects 기부자가 참여한 프로젝트 ID 목록
    * @return hasSetName 이름을 설정했는지 여부
     */
    function getDonorInfo(address _donor) public view returns (
        string memory name,
        uint256 totalDonated,
        uint256[] memory donatedProjects,
        bool hasSetName
    ) {
        return _getDonorInfo(donors, _donor);
    }
    
    /**
     * @dev 활성화된 프로젝트 목록 조회
     * @param _offset 시작 인덱스
     * @param _limit 조회할 항목 수
     * @return projectIds 프로젝트 ID 목록
     * @return names 프로젝트 이름 목록
     * @return targetAmounts 목표 금액 목록
     * @return raisedAmounts 현재까지 모금된 금액 목록
     * @return deadlines 프로젝트 마감일 목록
     */
    function getActiveProjects(uint256 _offset, uint256 _limit) public view returns (
        uint256[] memory projectIds,
        string[] memory names,
        uint256[] memory targetAmounts,
        uint256[] memory raisedAmounts,
        uint256[] memory deadlines
    ) {
        return _getActiveProjects(projects, projectCount, _offset, _limit);
    }
    
    /**
     * @dev 비상 상황 시 자금 회수
     */
    function emergencyWithdraw() public onlyOwner {
        _emergencyWithdraw(payable(owner()), address(this).balance);
    }
    
    /**
     * @dev 프로젝트 취소 및 환불
     * @param _projectId 프로젝트 ID
     */
    function cancelProjectAndRefund(uint256 _projectId) public onlyOwner {
        require(_projectId > 0 && _projectId <= projectCount, "Invalid project ID");
        _cancelProjectAndRefund(projects, projectDonations, _projectId);
        emit ProjectCancelled(_projectId);
    }
    
    /**
     * @dev ETH 직접 수신 가능
     */
    receive() external payable {
        // 플랫폼 주소로 직접 기부 (일반 기부가 아닌 경우)
        totalDonations += msg.value;
    }
}

contracts/DonationQuery.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./DonationStructs.sol";

/**
 * @title DonationQuery
 * @dev 기부 플랫폼의 데이터 조회 기능을 제공하는 컨트랙트
 * 프로젝트 정보, 기부 내역, 기부자 정보 등 조회 기능이 포함됩니다.
 */
contract DonationQuery {
    /**
     * @dev 프로젝트 정보 조회
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _getProjectDetails(
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 _projectId
    ) internal view returns (
        string memory name,
        string memory description,
        address recipient,
        uint256 targetAmount,
        uint256 raisedAmount,
        bool isActive,
        uint256 deadline,
        uint256 createTime,
        uint256 uniqueDonorCount
    ) {
        DonationStructs.Project storage project = projects[_projectId];
        return (
            project.name,
            project.description,
            project.recipient,
            project.targetAmount,
            project.raisedAmount,
            project.isActive,
            project.deadline,
            project.createTime,
            project.uniqueDonorCount
        );
    }
    
    /**
     * @dev 프로젝트의 기부 내역 조회
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _getProjectDonations(
        mapping(uint256 => DonationStructs.Donation[]) storage projectDonations,
        uint256 _projectId,
        uint256 _offset,
        uint256 _limit
    ) internal view returns (
        address[] memory donors,
        uint256[] memory amounts,
        uint256[] memory timestamps,
        bool[] memory anonymousFlags
    ) {
        DonationStructs.Donation[] storage donations = projectDonations[_projectId];
        
        uint256 resultCount = _limit;
        if (_offset + _limit > donations.length) {
            resultCount = donations.length > _offset ? donations.length - _offset : 0;
        }
        
        donors = new address[](resultCount);
        amounts = new uint256[](resultCount);
        timestamps = new uint256[](resultCount);
        anonymousFlags = new bool[](resultCount);
        
        for (uint256 i = 0; i < resultCount; i++) {
            DonationStructs.Donation storage donation = donations[_offset + i];
            donors[i] = donation.donor;
            amounts[i] = donation.amount;
            timestamps[i] = donation.timestamp;
            anonymousFlags[i] = donation.isAnonymous;
        }
        
        return (donors, amounts, timestamps, anonymousFlags);
    }
    
    /**
     * @dev 기부자 정보 조회
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _getDonorInfo(
        mapping(address => DonationStructs.Donor) storage donors,
        address _donor
    ) internal view returns (
        string memory name,
        uint256 totalDonated,
        uint256[] memory donatedProjects,
        bool hasSetName
    ) {
        DonationStructs.Donor storage donor = donors[_donor];
        return (donor.name, donor.totalDonated, donor.donatedProjects, donor.hasSetName);
    }
    
    /**
     * @dev 활성화된 프로젝트 목록 조회
     * 상세 구현은 DonationPlatform 컨트랙트에서 이루어집니다.
     */
    function _getActiveProjects(
        mapping(uint256 => DonationStructs.Project) storage projects,
        uint256 projectCount,
        uint256 _offset,
        uint256 _limit
    ) internal view returns (
        uint256[] memory projectIds,
        string[] memory names,
        uint256[] memory targetAmounts,
        uint256[] memory raisedAmounts,
        uint256[] memory deadlines
    ) {
        // 먼저 활성 프로젝트 수 계산
        uint256 activeCount = 0;
        for (uint256 i = 1; i <= projectCount; i++) {
            if (projects[i].isActive) {
                activeCount++;
            }
        }
        
        uint256 resultCount = _limit;
        if (_offset >= activeCount) {
            resultCount = 0;
        } else if (_offset + _limit > activeCount) {
            resultCount = activeCount - _offset;
        }
        
        projectIds = new uint256[](resultCount);
        names = new string[](resultCount);
        targetAmounts = new uint256[](resultCount);
        raisedAmounts = new uint256[](resultCount);
        deadlines = new uint256[](resultCount);
        
        if (resultCount > 0) {
            uint256 activeIndex = 0;
            uint256 resultIndex = 0;
            
            for (uint256 i = 1; i <= projectCount && resultIndex < resultCount; i++) {
                if (projects[i].isActive) {
                    if (activeIndex >= _offset) {
                        projectIds[resultIndex] = i;
                        names[resultIndex] = projects[i].name;
                        targetAmounts[resultIndex] = projects[i].targetAmount;
                        raisedAmounts[resultIndex] = projects[i].raisedAmount;
                        deadlines[resultIndex] = projects[i].deadline;
                        resultIndex++;
                    }
                    activeIndex++;
                }
            }
        }
        
        return (projectIds, names, targetAmounts, raisedAmounts, deadlines);
    }
}

contracts/DonationStructs.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// 기부 플랫폼에서 사용하는 모든 데이터 구조 정의
library DonationStructs {
    
    // 기부 프로젝트(수혜자) 구조체
    struct Project {
        address payable recipient;
        string name;
        string description;
        uint256 targetAmount;
        uint256 raisedAmount;
        bool isActive;
        uint256 deadline;
        uint256 createTime;
        mapping(address => bool) donorExists;
        uint256 uniqueDonorCount;
    }

    // 기부 내역 구조체
    struct Donation {
        address donor;
        uint256 projectId;
        uint256 amount;
        uint256 timestamp;
        bool isAnonymous;
    }

    // 기부자 구조체
    struct Donor {
        string name;
        uint256 totalDonated;
        uint256[] donatedProjects;
        bool hasSetName;
    }

    // 인출 요청 구조체
    struct WithdrawalRequest {
        uint256 projectId;
        uint256 amount;
        uint256 requestTime;
        bool executed;
    }
}

contracts/DonationUtils.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @title DonationUtils
 * @dev 기부 플랫폼에서 사용하는 유틸리티 함수들을 모아놓은 라이브러리
 */
library DonationUtils {
    /**
     * @dev uint를 string으로 변환하는 함수
     * @param value 변환할 uint 값
     * @return 변환된 string 값
     */
    function toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        
        uint256 temp = value;
        uint256 digits;
        
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        
        return string(buffer);
    }
    
    /**
     * @dev 이더를 Wei 단위로 변환하는 함수
     * @param amount 변환할 이더 양 (예: 1.5)
     * @return 변환된 Wei 값
     */
    function toWei(uint256 amount) internal pure returns (uint256) {
        return amount * 1 ether;
    }
    
    /**
     * @dev Wei를 이더 단위로 변환하는 함수
     * @param amount 변환할 Wei 양
     * @return 변환된 이더 값
     */
    function fromWei(uint256 amount) internal pure returns (uint256) {
        return amount / 1 ether;
    }
    
    /**
     * @dev 두 문자열이 같은지 비교하는 함수
     * @param a 첫 번째 문자열
     * @param b 두 번째 문자열
     * @return 문자열이 같으면 true, 다르면 false
     */
    function stringsEqual(string memory a, string memory b) internal pure returns (bool) {
        return keccak256(abi.encodePacked(a)) == keccak256(abi.encodePacked(b));
    }
    
    /**
     * @dev 타임스탬프를 Date 문자열로 변환하는 함수
     * @param timestamp Unix 타임스탬프
     * @return 날짜 문자열 (예: '20250513')
     */
    function timestampToDateString(uint256 timestamp) internal pure returns (string memory) {
        // 이 함수는 실제로는 복잡한 계산이 필요하지만, 
        // 여기서는 간단한 구현만 제공합니다.
        // 실제 구현에서는 더 정교한 날짜 계산이 필요할 수 있습니다.
        return toString(timestamp);
    }
}

